import { UIKitButtonComponent } from './ui-kit-button.component';
export default {
    title: 'UI Kit/Button',
    component: UIKitButtonComponent,
    parameters: {
        layout: 'centered',
    },
    argTypes: {
        variant: {
            control: 'select',
            options: ['primary', 'secondary', 'success', 'warning', 'danger', 'ghost'],
        },
        size: {
            control: 'select',
            options: ['sm', 'md', 'lg'],
        },
        disabled: {
            control: 'boolean',
        },
    },
};
export const Primary = {
    args: {
        variant: 'primary',
        size: 'md',
        disabled: false,
    },
    render: (args) => ({
        props: args,
        template: `<ui-button [variant]="variant" [size]="size" [disabled]="disabled">Primary Button</ui-button>`,
    }),
};
export const AllVariants = {
    render: () => ({
        template: `
      <div style="display: flex; gap: 8px; flex-wrap: wrap;">
        <ui-button variant="primary">Primary</ui-button>
        <ui-button variant="secondary">Secondary</ui-button>
        <ui-button variant="success">Success</ui-button>
        <ui-button variant="warning">Warning</ui-button>
        <ui-button variant="danger">Danger</ui-button>
        <ui-button variant="ghost">Ghost</ui-button>
      </div>
    `,
    }),
};
