import { UIKitDropdownComponent } from './ui-kit-dropdown.component';
const meta = {
    title: 'UI Kit/Form Controls/Dropdown',
    component: UIKitDropdownComponent,
    parameters: {
        layout: 'padded',
        docs: {
            description: {
                component: 'A reusable dropdown component for FleetOps with consistent styling and behavior.'
            }
        }
    },
    argTypes: {
        label: { control: 'text' },
        placeholder: { control: 'text' },
        value: { control: 'text' },
        disabled: { control: 'boolean' },
        hasError: { control: 'boolean' },
        errorMessage: { control: 'text' },
        options: { control: 'object' }
    }
};
export default meta;
const carrierOptions = [
    { value: 'bluedart', label: 'BlueDart Express - 1-2 Days' },
    { value: 'dtdc', label: 'DTDC Courier - 2-3 Days' },
    { value: 'fedex', label: 'FedEx - 1-2 Days' },
    { value: 'dhl', label: 'DHL Express - 1-2 Days' },
    { value: 'ecom', label: 'Ecom Express - 3-5 Days' }
];
export const Default = {
    args: {
        label: 'Select Carrier',
        placeholder: 'Choose a carrier...',
        options: carrierOptions,
        value: '',
        disabled: false,
        hasError: false,
        errorMessage: ''
    }
};
export const WithValue = {
    args: {
        label: 'Select Carrier',
        placeholder: 'Choose a carrier...',
        options: carrierOptions,
        value: 'bluedart',
        disabled: false,
        hasError: false,
        errorMessage: ''
    }
};
export const WithError = {
    args: {
        label: 'Select Carrier',
        placeholder: 'Choose a carrier...',
        options: carrierOptions,
        value: '',
        disabled: false,
        hasError: true,
        errorMessage: 'Please select a carrier'
    }
};
export const Disabled = {
    args: {
        label: 'Select Carrier',
        placeholder: 'Choose a carrier...',
        options: carrierOptions,
        value: '',
        disabled: true,
        hasError: false,
        errorMessage: ''
